								<div class='ipsData__desc ipsRichText i-margin-top_2 ipsTruncate_4'>
									{$comment->truncated( true )|raw}
								</div>
							</div>
						</li>
					{{endforeach}}
				</ul>
			</i-data>
		</div>
	{{else}}
		<div class='ipsWidget__content'>
			{{foreach $comments as $comment}}
				<section class='ipsEntry ipsEntry--simple'>
					<div class='ipsEntry__content'>
						<header class='ipsEntry__header'>
							<div class='ipsEntry__header-align'>
								<div class='ipsPhotoPanel'>
									{template="userPhoto" app="core" group="global" params="$comment->author(), 'mini', $comment->warningRef()"}
									<div class='ipsPhotoPanel__text'>
										<h3 class='ipsPhotoPanel__primary i-color_hard'>
											{template="userLink" app="core" group="global" params="$comment->author(), $comment->warningRef(), NULL, $comment->isAnonymous()"}
										</h3>
										<p class='ipsPhotoPanel__secondary'>
											<a href='{$comment->item()->url()->setQueryString( array( 'do' => 'findComment', 'comment' => $comment->id ) )}' class='i-color_inherit'>{$comment->dateLine()|raw}</a>
											{{if $comment->editLine()}}
												({lang="edited_lc"})
											{{endif}}
											{{if $comment->hidden()}}
												&middot; {$comment->hiddenBlurb()}
											{{endif}}
										</p>
									</div>
								</div>
							</div>
						</header>
						<div class='ipsEntry__post'>

							{{if member.modPermission('mod_see_warn') and $comment->warning}}
								{template="commentWarned" group="global" app="core" params="$comment"}
							{{endif}}

							{{if \IPS\IPS::classUsesTrait( $comment, 'IPS\Content\Reactable' ) and $comment->isHighlighted()}}
								<ul class='ipsBadges'>
									<li><span class='ipsBadge ipsBadge--popular'>{lang="this_is_a_popular_comment"}</span></li>
								</ul>
							{{endif}}

							<div class='i-margin-bottom_2 i-font-weight_500 i-font-size_2'><a href="{$comment->url()}" class='i-color_hard'>{$comment->item()->name}</a></div>
							<div data-role='commentContent' class='ipsRichText ' data-controller='core.front.core.lightboxedImages'>
								{{if $comment->hidden() === 1 && $comment->author()->member_id == \IPS\Member::loggedIn()->member_id}}
									<strong class='i-color_warning'><i class='fa-solid fa-circle-info'></i> {lang="comment_awaiting_approval"}</strong>
								{{endif}}
								{$comment->content()|raw}

								{{if $comment->editLine()}}
									{$comment->editLine()|raw}
								{{endif}}
							</div>
						</div>
						<div class='ipsEntry__footer'>
							<menu class='ipsEntry__controls' data-role="commentControls">
								{{if $comment->canReportOrRevoke() === TRUE}}
									<li><a href='{$comment->url('report')}' {{if member.member_id or \IPS\Helpers\Form\Captcha::supportsModal()}}data-ipsDialog data-ipsDialog-remoteSubmit data-ipsDialog-size='medium' data-ipsDialog-flashMessage='{lang="report_submit_success"}' data-ipsDialog-title="{lang="report"}"{{endif}} data-action='reportComment' title='{lang="report_content"}'>{lang="report"}</a></li>
								{{endif}}
							</menu>
							{{if $comment->hidden() !== 1 && \IPS\IPS::classUsesTrait( $comment, 'IPS\Content\Reactable' ) and settings.reputation_enabled}}
								{template="reputation" group="global" app="core" params="$comment"}
							{{endif}}
						</div>
					</div>
				</section>
			{{endforeach}}
		</div>
	{{endif}}
{{endif}}

